CREATE TABLE `car` (
  `id` int NOT NULL COMMENT 'ID автомобиля',
  `name_car` varchar(255) NOT NULL COMMENT 'Название авто',
  `photo_car_1` varchar(255) NOT NULL COMMENT 'Фото авто 1',
  `photo_car_2` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT 'Фото авто 2',
  `photo_car_3` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT 'Фото авто 3',
  `id_user` int NOT NULL COMMENT 'Владелец авто',
  `id_bodywork` int NOT NULL COMMENT 'Кузов авто',
  `id_transmission_car` int NOT NULL COMMENT 'Коробка авто',
  `id_color` int NOT NULL COMMENT 'Цвет авто',
  `id_drive_car` int NOT NULL COMMENT 'Привод авто',
  `id_brand_car` int NOT NULL COMMENT 'Модель/марка авто',
  `id_engine_car` int NOT NULL COMMENT 'Двигатель авто',
  `modification_car` char(3) NOT NULL COMMENT 'Модификация',
  `id_owners` int NOT NULL COMMENT 'Кол-во владельцев авто',
  `id_pts` int NOT NULL COMMENT 'ПТС авто',
  `id_year_of_release` int NOT NULL COMMENT 'Год выпуска авто',
  `id_type_of_car` int NOT NULL COMMENT 'Тип авто',
  `id_used_or_new` int NOT NULL,
  `price` char(255) NOT NULL COMMENT 'Цена',
  `mileage` char(255) NOT NULL COMMENT 'Пробег',
  `comments_user` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT 'Комментарии от владельца',
  `views_post` int NOT NULL DEFAULT '0',
  `date_of_registration_post_car` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'Дата публикации авто',
  `id_status` int NOT NULL DEFAULT '1' COMMENT 'Статус'
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

